using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;using teruzuki.Twitter;public class SplashManager : MonoBehaviour{	private IEnumerator initializeCoroutine;	private Transform logoTransform;	public void Initialize (Transform transform)	{		logoTransform = transform;		initializeCoroutine = initialize ();		StartCoroutine (initializeCoroutine);	}	void Update() {		if(logoTransform != null)		{			logoTransform.Rotate (Vector3.up, Time.deltaTime * 100.0f, Space.World);		}	}	private IEnumerator initialize ()	{		if (TokenManager.Instance.TokenList.Count > 0)		{			SceneManager.LoadScene ("main");		}		else		{			SceneManager.LoadScene ("account");		}		yield return null;	}}